
/**
 * Module dependencies.
 */

var http = require('http')
  , cluster = require('./');

var server = http.createServer(function(req, res){
  res.end('Hello World');
});

cluster(server)
  .set('workers', 8)
  .use(cluster.debug())
  .use(cluster.stats({ connections: true, requests: true }))
  .listen(3000);
